import { ref } from 'vue';
export default {
  props: {
    title: String,
    message: String,
    yes: {
      type: String,
      default: 'Oui'
    },
    no: {
      type: String,
      default: 'Non'
    }
  },
  emits: ['handleYes', 'handleNo'],
  setup(props, {
    emit
  }) {
    const dialog = ref(false);
    function handleYes() {
      emit("handleYes");
      dialog.value = false;
    }
    function handleNo() {
      emit("handleNo");
      dialog.value = false;
    }
    return {
      dialog,
      handleYes,
      handleNo
    };
  }
};
